import jsonimport timefrom tkinter import *Total_Points_Attaque = 0Total_Points_Defense = 0Total_Points_Passe = 0Total_Points_Contre = 0Total_Points_Service = 0 Liste_Points_Attaque = []Liste_Points_Defense = []Liste_Points_Passe = []Liste_Points_Contre = []Liste_Points_Service = [] Bibli_Points_Attaque = dict()Bibli_Points_Defense = dict()Bibli_Points_Passe = dict()Bibli_Points_Contre = dict()Bibli_Points_Service = dict()groupe = "" # get input tkinternom = "" # get input tkinterprenom = "" # get input tkinterdef save():    data = \    {        "qualites": [            {                "attaque": {"total": Total_Points_Attaque, "liste": Liste_Points_Attaque, "actions": Bibli_Points_Attaque}            },            {                "defense": {"total": Total_Points_Defense, "liste": Liste_Points_Defense, "actions": Bibli_Points_Defense}            },            {                "passe": {"total": Total_Points_Passe, "liste": Liste_Points_Passe, "actions": Bibli_Points_Passe}            },            {                "contre": {"total": Total_Points_Contre, "liste": Liste_Points_Contre, "actions": Bibli_Points_Contre}            },            {                "service": {"total": Total_Points_Service, "liste": Liste_Points_Service, "actions": Bibli_Points_Service}            }        ],        "cheminGraphRadar": "c:\\Volley\\",        "nom" : nom,        "prenom" : prenom,        "groupe" : groupe,        "date" : time.strftime("%d/%m/%Y")    }    jsonData = json.dumps(data, ensure_ascii=False)    # http://tkinter.unpythonic.net/wiki/tkFileDialog    file = filedialog.asksaveasfile(mode='w', title = "Sauvegarde", defaultextension = ".json", filetypes = (("json files","*.json"),("all files","*.*")))    if file is None:         return    file.write(jsonData)    #print(jsondata)def load():    # http://tkinter.unpythonic.net/wiki/tkFileDialog    file = filedialog.askopenfile(mode='r', title = "Chargement", defaultextension = ".json", filetypes = (("json files","*.json"),("all files","*.*")))    if file is None:         return    jsonData = file.read()    data = json.loads(jsonData)    print(data)    #print(data["qualites"])    #print(data["qualites"][0])    #print(data["qualites"][0]["attaque"])    #print(data["qualites"][0]["attaque"]["total"])    #print(data["qualites"][0]["attaque"]["liste"])    #print(data["qualites"][0]["attaque"]["actions"])fenetre = Tk()Button(fenetre, text = "Save", command = lambda:save()).grid(row = 0, column = 0)Button(fenetre, text = "Load", command = lambda:load()).grid(row = 0, column = 1)fenetre.mainloop()